    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}


{% block title %}Wow Chronicles - Se Connecter{% endblock %}

{% block body %}
<form method=\"post\">
    {% if error %}
        <div class=\"alert alert-danger\">{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

    <h1 class=\" mb-3 font-weight-normal\">S'identifier</h1>

    <div class=\"espaceCustom\">
        <label for=\"inputEmail\">Email :</label>
        <input type=\"email\" value=\"{{ last_username }}\" placeholder=\"Merci de rentrer votre email\" name=\"email\" id=\"inputEmail\" class=\"form-control\" autocomplete=\"email\" required autofocus>
    </div>

    <div class=\"espaceCustom\" >
        <label for=\"inputPassword\">Mot de passe :</label>
        <input type=\"password\" placeholder=\"Merci de rentrer votre mot de passe\" name=\"password\" id=\"inputPassword\" class=\"form-control\" autocomplete=\"current-password\" required>
    </div>

    <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

    {#
        Uncomment this section and add a remember_me option below your firewall to activate remember me functionality.
        See https://symfony.com/doc/current/security/remember_me.html

        <div class=\"checkbox mb-3\">
            <label>
                <input type=\"checkbox\" name=\"_remember_me\"> Remember me
            </label>
        </div>
    #}

    <a class=\"espaceCustom\" href=\"/reset-password\">Mot de passe oublié ?</a>

    <br>

    <button class=\"btn btn-primary espaceCustom\" type=\"submit\" style=\"background-color: #343A40; color: white; margin-top: 20px \">Se connecter</button>
</form>
{% endblock %}
", "security/login.html.twig", "D:\\Fichiers\\Documents\\IUT\\Cours\\Annee_2\\M3104\\TP\\SiteWow\\templates\\security\\login.html.twig");
