            <tr>
                <td>{{ type.nom }}</td>
                <td>{{ type.descriptionType | u.truncate(100,'[...]',false) | raw }}</td>
                <td>
                    <a href=\"{{ path('type_edit', {'id': type.id}) }}\">Editer</a>
                </td>
            </tr>
